];

        let canvas = document.getElementById('gameboard');
        let ctx = canvas.getContext('2d');
        let size = canvas.getBoundingClientRect();
        canvas.width = size.width * 10;
        canvas.height = size.height * 10;
        ctx.scale(10, 10);

        let spawn;
        let goal;
        
        let keys = {
            w: false,
            a: false,
            s: false,
            d: false
        };

        let game = {
            speed: 1000/20,
            w:80,
            h:40,
            blocklength: 0,
        };
        game.blocklength = size.width/game.w;

        //Styling stuff
        window.addEventListener('resize', resize);
        function resize(){
            let viewportWidth = window.innerWidth;
            let viewportHeight = window.innerHeight;
            if (canvas.offsetWidth > viewportWidth || canvas.offsetHeight > viewportHeight) {
                canvas.style.left = "0%";
                canvas.style.top = "0%";
                canvas.style.transform = "translate(0%, 0%)";
            } else {
                canvas.style.left = "50%";
                canvas.style.top = "50%";
                canvas.style.transform = "translate(-50%, -50%)";
            }
        }
        resize();

        //Drawing unmoving
        function drawwalls(){
            for (var y = 0; y < game.h; y++){
                for (var x = 0; x < game.w; x++){
                    switch(mapgen[y*game.w+x]) {
                        case 1:
                            ctx.fillStyle = 'black';
                            ctx.fillRect(x*game.blocklength, y*game.blocklength, game.blocklength, game.blocklength); 
                            break;
                        case 2:
                            ctx.fillStyle = 'red';
                            ctx.fillRect(x*game.blocklength, y*game.blocklength, game.blocklength, game.blocklength);
                            spawn = {x:x*game.blocklength, y:y*game.blocklength};
                            break;
                        case 3:
                            ctx.fillStyle = 'blue';
                            ctx.fillRect(x*game.blocklength, y*game.blocklength, game.blocklength, game.blocklength);
                            goal = {x:x, y:y};
                            break;
                    }
                } 
            }
        }
        drawwalls();

        let player = {
            size: game.blocklength, 
            x: spawn.x,
            y: spawn.y,
            vx: 0,
            vy: 0,
            move: game.blocklength/4,
            color: "green",
        };
        //spawn
        function spawnplayer(){
            ctx.fillStyle = player.color; 
            ctx.fillRect(spawn.x, spawn.y, player.size, player.size);
        }
        spawnplayer();

        window.addEventListener('keydown', function(evt) {
            keys[evt.key] = true;
        });
        window.addEventListener('keyup', function(evt) {
            keys[evt.key] = false;
        });

        //gameloop
        
        let fps = 30;
        let now;
        let then = Date.now();
        let interval = 1000/fps;
        let delta;
        // let x = 0;
        // setInterval(() => {
        //         console.log(x);
        //         x=0;
        //     }, 1000);
        function gameloop(){
            now = Date.now();
            delta = now - then;
            if (delta > interval) {
                // x=x+1;
                then = now - (delta % interval);
                animatecharacter();
                checkcollision();
            }
            requestAnimationFrame(gameloop);
        }

        function animatecharacter(){
            ctx.clearRect(player.x, player.y, player.size, player.size);
            if(keys.w == true){
                player.y = player.y-player.move;
            }
            if(keys.s == true){
                player.y = player.y+player.move;
            }
            if(keys.a == true){
                player.x = player.x-player.move;
            }
            if(keys.d == true){
                player.x = player.x+player.move;
            }
            ctx.fillStyle = player.color;
            ctx.fillRect(player.x, player.y, player.size, player.size);
        }
        requestAnimationFrame(gameloop);

        let bordcord;
        function checkcollision(){
            bordcord = [player.y/game.blocklength*game.h + player.x/game.blocklength];
            console.log(bordcord);
            // fix this dude like wtf
        }

    });
</script>